/reducers.js.code:
const initialState = {
  tasks: [],
};

const taskReducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_TASK":
      return {
        ...state,
        tasks: [...state.tasks, action.payload],
      };
    case "REMOVE_TASK":
      // Bug: Use action.payload.id not action.id
        return {
        ...state,
          tasks: state.tasks.filter((task) => task.id !== action.payload.id),
        };
    default:
      return state;
  }
};

export default taskReducer;

