import React from "react";
import { render, fireEvent } from "@testing-library/react";
import "@testing-library/jest-dom";
import { createMemoryHistory } from "history";
import { createBrowserRouter, RouterProvider } from "react-router-dom";
import App from "../App";

test("renders App component", () => {
  render(<App />);
});

test("navigates to Welcome page", () => {
  const { getByText } = render(<App />);

  // Assuming there is a text unique to the Welcome page
  const welcomeText = getByText(/WELCOME/i);
  expect(welcomeText).toBeInTheDocument();
});

test("navigates to About page", () => {
  const { getByText } = render(<App />);

  // Assuming there is a text unique to the About page
  fireEvent.click(getByText(/About/));
  const aboutText = getByText(/ABOUT/)
  expect(aboutText).toBeInTheDocument();
});

test("navigates to About page", () => {
  const { getByText } = render(<App />);

  // Assuming there is a text unique to the About page
  fireEvent.click(getByText(/Info/));
  const aboutText = getByText(/INFO/)
  expect(aboutText).toBeInTheDocument();
});

