    { id: 4, name: 'TV', price: 200, popularity: 4, rating: 4.4 },
];

function updateTable(sortedProducts) {
  let tableBody = document
    .getElementById("productTable")
    .getElementsByTagName("tbody")[0];
  tableBody.innerHTML = "";

  sortedProducts.forEach((product) => {
    let row = tableBody.insertRow();
    for (let key in product) {
      let cell = row.insertCell();
      cell.appendChild(document.createTextNode(product[key]));
    }
  });
}

function sortData(algo) {
  let sortedProducts;
  switch (algo) {
    case "reset":
      sortedProducts = [...products];
      break;
    case "bubble":
      sortedProducts = bubbleSort([...products]);
      break;
    case "insertion":
      sortedProducts = insertionSort([...products]);
      break;
    case "selection":
      sortedProducts = selectionSort([...products]);
      break;
    case "merge":
      sortedProducts = mergeSort([...products]);
      break;
    case "quick":
      sortedProducts = quickSort([...products]);
      break;
    case "heap":
      sortedProducts = heapSort([...products]);
      break;
    case "shell":
      sortedProducts = shellSort([...products]);
      break;
    default:
      sortedProducts = bubbleSort([...products]);
      break;
  }

  updateTable(sortedProducts);
