/sortingAlgorithms/bubbleSort.js.code:
// O(n^2)
function bubbleSort(arr) {
  let n = arr.length;
  for (let i = 0; i < n - 1; i++) {
    for (let j = 0; j < n - i - 1; j++) {
      if (arr[j].id > arr[j + 1].id) {
        let temp = arr[j];
        arr[j] = arr[j + 1];
        arr[j + 1] = temp;
      }
    }
  }
  return arr;
}

/sortingAlgorithms/heapSort.js.code:
function heapSort(arr) {
  const n = arr.length;

  // Build a max heap
  for (let i = Math.floor(n / 2) - 1; i >= 0; i--) {
    heapify(arr, n, i, "id");
  }

  // Extract elements from the heap one by one
  for (let i = n - 1; i > 0; i--) {
    // Swap the root (max element) with the last element
    const temp = arr[0];
    arr[0] = arr[i];
    arr[i] = temp;

    // Call heapify on the reduced heap
    heapify(arr, i, 0);
  }

  return arr;
}

// Helper function to heapify a subtree rooted at index i
function heapify(arr, n, i) {
  let largest = i;
  const left = 2 * i + 1;
  const right = 2 * i + 2;

  // Compare with left child
  if (left < n && arr[left].id > arr[largest].id) {
    largest = left;
  }

  // Compare with right child
  if (right < n && arr[right].id > arr[largest].id) {
    largest = right;
  }

  // If the largest is not the root, swap and recursively heapify the affected subtree
  if (largest !== i) {
    const temp = arr[i];
    arr[i] = arr[largest];
    arr[largest] = temp;

    heapify(arr, n, largest);
  }
}

/sortingAlgorithms/insertionSort.js.code:
function insertionSort(arr) {
  let n = arr.length;

  for (let i = 1; i < n; i++) {
    let currentElement = arr[i];
    let j = i - 1;

    while (j >= 0 && arr[j].id > currentElement.id) {
      arr[j + 1] = arr[j];
      j--;
    }

    arr[j + 1] = currentElement;
  }

  return arr;
}

/sortingAlgorithms/mergeSort.js.code:
function mergeSort(arr) {
  if (arr.length <= 1) {
    return arr;
  }

  // Split the array into two halves
  const middle = Math.floor(arr.length / 2);
  const leftHalf = arr.slice(0, middle);
  const rightHalf = arr.slice(middle);

  // Recursively apply mergeSort to the two halves
  const sortedLeft = mergeSort(leftHalf);
  const sortedRight = mergeSort(rightHalf);

  // Merge the sorted halves
  return merge(sortedLeft, sortedRight);
}

// Helper function to merge two sorted arrays
function merge(left, right) {
  let result = [];
  let leftIndex = 0;
  let rightIndex = 0;

  while (leftIndex < left.length && rightIndex < right.length) {
    if (left[leftIndex].id < right[rightIndex].id) {
      result.push(left[leftIndex]);
      leftIndex++;
    } else {
      result.push(right[rightIndex]);
      rightIndex++;
    }
  }

  // Concatenate any remaining elements from both arrays
  return result.concat(left.slice(leftIndex), right.slice(rightIndex));
}

/sortingAlgorithms/quickSort.js.code:
function quickSort(arr) {
  if (arr.length <= 1) {
    return arr;
  }

  const pivot = arr[0];
  const left = [];
  const right = [];

  for (let i = 1; i < arr.length; i++) {
    if (arr[i].id < pivot.id) {
      left.push(arr[i]);
    } else {
      right.push(arr[i]);
    }
  }

  return quickSort(left).concat(pivot, quickSort(right));
}

/sortingAlgorithms/selectionSort.js.code:
function selectionSort(arr) {
  let n = arr.length;

  for (let i = 0; i < n - 1; i++) {
    let minIndex = i;

    for (let j = i + 1; j < n; j++) {
      if (arr[j].id < arr[minIndex].id) {
        minIndex = j;
      }
    }

    if (minIndex !== i) {
      let temp = arr[i];
      arr[i] = arr[minIndex];
      arr[minIndex] = temp;
    }
  }

  return arr;
}

/sortingAlgorithms/shellSort.js.code:
function shellSort(arr) {
  const n = arr.length;

  // Start with a large gap and reduce it
  for (let gap = Math.floor(n / 2); gap > 0; gap = Math.floor(gap / 2)) {
    // Perform a gapped insertion sort for this gap size
    for (let i = gap; i < n; i++) {
      const currentElement = arr[i];

      // Shift the elements until the correct position is found
      let j;
      for (j = i; j >= gap && arr[j - gap].id > currentElement.id; j -= gap) {
        arr[j] = arr[j - gap];
      }

      // Place the current element at its correct position
      arr[j] = currentElement;
    }
  }

  return arr;
}

