import React, { useState, useContext, createContext } from "react";

const FormContext = createContext();

const FormProvider = ({ children }) => {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    step1: {
      firstName: "",
      lastName: "",
      email: "",
    },
    step2: {
      address: "",
      city: "",
      zipCode: "",
    },
    step3: {
      paymentMethod: "",
      cardNumber: "",
      expirationDate: "",
    },
  });

  const handleInputChange = (step, fieldName, value) => {
    setFormData((prevData) => ({
      ...prevData,
      [step]: {
        ...prevData[step],
        [fieldName]: value,
      },
    }));
  };

  const fields = [
    [
      {
        label: "First Name:",
        id: "firstName",
        value: formData.step1.firstName || "",
        onChange: (e) =>
          handleInputChange("step1", "firstName", e.target.value),
      },
      {
        label: "Last Name:",
        id: "lastName",
        value: formData.step1.lastName || "",
        onChange: (e) => handleInputChange("step1", "lastName", e.target.value),
      },
    ],
    [
      {
        label: "City:",
        id: "city",
        value: formData.step2.city || "",
        onChange: (e) => handleInputChange("step2", "city", e.target.value),
      },
      {
        label: "Zip Code:",
        id: "zipCode",
        value: formData.step2.zipCode || "",
        onChange: (e) => handleInputChange("step2", "zipCode", e.target.value),
      },
    ],
    [
      {
        label: "Favorite Food:",
        id: "favoriteFood",
        value: formData.step3.favoriteFood || "",
        onChange: (e) =>
          handleInputChange("step3", "favoriteFood", e.target.value),
      },
      {
        label: "Favorite Dog:",
        id: "favoriteDog",
        value: formData.step3.favoriteDog || "",
        onChange: (e) =>
          handleInputChange("step3", "favoriteDog", e.target.value),
      },
    ],
  ];

  const handleSubmit = () => {
    console.log("Form submitted:", formData);
  };

  const contextValue = {
    currentStep,
    formData,
    handleInputChange,
    setCurrentStep,
    handleSubmit,
    fields,
  };
  return (
    <FormContext.Provider value={contextValue}>{children}</FormContext.Provider>
  );
};

export { FormContext, FormProvider };

