import { useContext } from "react";
import { FormContext } from "../FormContext.jsx";

const Navigation = () => {
  const { currentStep, setCurrentStep, handleSubmit } = useContext(FormContext);
  return (
    <div className="nav-buttons">
      {currentStep > 1 && (
        <button onClick={() => setCurrentStep((prevStep) => prevStep - 1)}>
          Previous
        </button>
      )}
      {currentStep < 3 && (
        <button onClick={() => setCurrentStep((prevStep) => prevStep + 1)}>
          Next
        </button>
      )}
      {currentStep === 3 && <button onClick={handleSubmit}>Submit</button>}
    </div>
  );
};

