/Form.js.code:
import { useState, useRef } from "react";

const inputs = [
  {
    label: "Username",
    name: "username",
    type: "text",
    placeholder: "Enter your username",
  },
  {
    label: "Password",
    name: "password",
    type: "password",
    placeholder: "Enter a strong password",
  },
  // {
  //   label: "Profile Image",
  //   name: "profileImage",
  //   type: "file",
  // },
];

const defaultFormData = inputs.reduce((acc, input) => {
  const name = input.name;
  acc[name] = "";
  return acc;
}, {});

const Form = ({ setUser }) => {
  const fileInput = useRef(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [imagePreview, setImagePreview] = useState(null);
  const [formData, setFormData] = useState(defaultFormData);

  const handleInputChange = (evt) => {
    const { name, value, files, type } = evt.target;

    if (type === "file") {
      const file = files[0];
      if (file) {
        setFormData((prevState) => ({
          ...prevState,
          [name]: file,
        }));
        setImagePreview(URL.createObjectURL(file));
      }
    } else {
      setFormData((prevState) => ({
        ...prevState,
        [name]: value,
      }));
    }
  };

  const handleSubmit = (evt) => {
    evt.preventDefault();
    setLoading(true);

    const formDataToSend = new FormData();

    for (const key in formData) {
      formDataToSend.append(key, formData[key]);
    }

    fetch("/api/sign-up", {
      method: "POST",
      body: formDataToSend,
    })
      .then(async (response) => {
        const json = await response.json();
        setUser(json.user);
      })
      .catch(async (error) => {
        const json = await error.json();

        setError(json.error);
      })
      .finally(() => {
        setLoading(false);
      });
  };

  const handleClickImage = () => {
    fileInput.current.click();
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <p>Sign Up</p>
        <i className="material-icons">cloud</i>
        <hr />
      </div>
      <div className="image-container">
        {imagePreview ? (
          <>
            <img
              src={imagePreview}
              alt="No image"
              style={{
                width: "100px",
                height: "100px",
                borderRadius: "50%",
                cursor: "pointer",
                fontSize: "10px",
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                border: "solid green 1px",
              }}
            />
          </>
        ) : (
          <>
            <div>
              <span className="material-symbols-outlined">add_a_photo</span>{" "}
              <div className="image-input" onClick={handleClickImage}></div>
              <input ref={fileInput} type="file" style={{ display: "none" }} />
            </div>
          </>
        )}
      </div>
      {inputs.map((input) => (
        <div key={input.name} className="input-wrapper">
          <label htmlFor={input.label}>{input.label}:</label>
          <input
            id={input.label}
            name={input.name}
            type={input.type}
            autoComplete="off"
            value={input.type === "file" ? undefined : formData[input.name]}
            accept={input.type === "file" ? "image/*" : undefined}
            onChange={handleInputChange}
            placeholder={input.placeholder}
          />
        </div>
      ))}

      <input
        type="submit"
        disabled={loading}
        value={loading ? "Loading..." : "Sign Up"}
      />
      {error && (
        <div className="error-alert">
          <p>Error: {error}</p>
        </div>
      )}
    </form>
  );
};

export default Form;

