import React, { useState, useEffect } from "react";
import FilterModal from "./FilterModal";
import TableHead from "./table/TableHead";
import Search from "./table/Search";
import Filter from "./table/Filter";

const endpointUrl = "https://data.webdevinterviews.com/dogBreeds.json";

const DogTable = () => {
  const [breeds, setBreeds] = useState([]);
  const [originalBreeds, setOriginalBreeds] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filterModalIsOpen, setFilterModalIsOpen] = useState(false);

  const [filterOptions, setFilterOptions] = useState({
    avgWeight: "",
    avgLifespan: "",
    avgHeight: "",
    isActive: "",
    weightComparison: "greaterThan",
    lifespanComparison: "greaterThan",
    heightComparison: "greaterThan",
  });

  useEffect(() => {
    setLoading(true);
    fetch(endpointUrl)
      .then(async (response) => {
        const data = await response.json();
        setBreeds(data);
        setOriginalBreeds(data);
        setLoading(false);
      })
      .catch((e) => {
        console.log(e);
        setLoading(false);
      });
  }, []);

  const handleFilterButtonClick = () => {
    setFilterModalIsOpen(true);
  };

  const handleFilterModalClose = () => {
    setFilterModalIsOpen(false);
  };

  if (loading) {
    return <div>Loading</div>;
  }

  return (
    <>
      <div className="table-actions">
        <Search dogBreeds={originalBreeds} setBreeds={setBreeds} />
        <Filter handleFilterButtonClick={handleFilterButtonClick} />
      </div>
      <table>
        <TableHead breeds={breeds} setBreeds={setBreeds} />
        <tbody>
          {breeds.map((breed) => (
            <tr key={breed.id}>
              <td>{breed.id}</td>
              <td>{breed.name}</td>
              <td>{breed.avgWeight} lbs.</td>
              <td>{breed.avgLifespan} years</td>
              <td>{breed.avgHeight} inches</td>
              <td>{breed.isActive ? "Yes" : "No"}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {filterModalIsOpen && (
        <FilterModal
          dogBreeds={originalBreeds}
          setBreeds={setBreeds}
          setFilterOptions={setFilterOptions}
          filterOptions={filterOptions}
          handleFilterModalClose={handleFilterModalClose}
        />
      )}
    </>
  );
};

export default DogTable;

