import { useState } from "react";
import CustomInput from "./CustomInput";

const FilterModal = ({
  handleFilterModalClose,
  dogBreeds,
  setBreeds,
  filterOptions,
  setFilterOptions,
}) => {
  const handleApplyFilters = () => {
    let filteredBreeds = dogBreeds;

    if (filterOptions.avgWeight !== "") {
      const weightComparison = filterOptions.weightComparison;

      filteredBreeds = filteredBreeds.filter((breed) => {
        const breedWeight = breed.avgWeight;
        const filterWeight = parseFloat(filterOptions.avgWeight);

        if (weightComparison === "lessThan") {
          return breedWeight < filterWeight;
        } else if (weightComparison === "greaterThan") {
          return breedWeight > filterWeight;
        } else {
          return breedWeight === filterWeight;
        }
      });
    }

    if (filterOptions.avgLifespan !== "") {
      const lifespanComparison = filterOptions.lifespanComparison;

      filteredBreeds = filteredBreeds.filter((breed) => {
        const breedLifespan = breed.avgLifespan;
        const filterLifespan = parseFloat(filterOptions.avgLifespan);

        if (lifespanComparison === "lessThan") {
          return breedLifespan < filterLifespan;
        } else if (lifespanComparison === "greaterThan") {
          return breedLifespan > filterLifespan;
        } else {
          return breedLifespan === filterLifespan;
        }
      });
    }

    if (filterOptions.avgHeight !== "") {
      const heightComparison = filterOptions.heightComparison;

      filteredBreeds = filteredBreeds.filter((breed) => {
        const breedHeight = breed.avgHeight;
        const filterHeight = parseFloat(filterOptions.avgHeight);

        if (heightComparison === "lessThan") {
          return breedHeight < filterHeight;
        } else if (heightComparison === "greaterThan") {
          return breedHeight > filterHeight;
        } else {
          return breedHeight === filterHeight;
        }
      });
    }

    if (filterOptions.isActive !== "") {
      const isActiveFilter = filterOptions.isActive === "true";
      filteredBreeds = filteredBreeds.filter(
        (breed) => breed.isActive === isActiveFilter
      );
    }

    setBreeds(filteredBreeds);
    handleFilterModalClose();
  };

  const handleFilterChange = (key, value) => {
    setFilterOptions((prevOptions) => ({ ...prevOptions, [key]: value }));
  };

  const handleResetFilters = () => {
    setFilterOptions({
      avgWeight: "",
      avgLifespan: "",
      avgHeight: "",
      isActive: "",
      weightComparison: "greaterThan",
      lifespanComparison: "greaterThan",
      heightComparison: "greaterThan",
    });
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content filter-modal">
        <div className="filter-title">
          <h2>Filter Options</h2>
          <button onClick={handleFilterModalClose}>X</button>
        </div>
        <CustomInput
          filterOptions={filterOptions}
          handleFilterChange={handleFilterChange}
          label="Average Weight"
          keyValue="avgWeight"
          selectKey="weightComparison"
        />
        <CustomInput
          filterOptions={filterOptions}
          handleFilterChange={handleFilterChange}
          label="Average Lifespan"
          keyValue="avgLifespan"
          selectKey="lifespanComparison"
        />
        <CustomInput
          filterOptions={filterOptions}
          handleFilterChange={handleFilterChange}
          label="Average Height"
          keyValue="avgHeight"
          selectKey="heightComparison"
        />
        <div>
          <label htmlFor="isActive">Active Breed:</label>
          <select
            id="isActive"
            value={filterOptions.isActive}
            onChange={(e) => handleFilterChange("isActive", e.target.value)}
          >
            <option value="">Any</option>
            <option value="true">Yes</option>
            <option value="false">No</option>
          </select>
        </div>
        <div className="modal-footer">
          <button className="reset-button" onClick={handleResetFilters}>
            Reset Filters
          </button>
          <button onClick={handleApplyFilters}>Apply Filters</button>
        </div>
      </div>
    </div>
  );
};

export default FilterModal;

