import { useState } from "react";

const TableHead = ({ breeds, setBreeds }) => {
  const [sortConfig, setSortConfig] = useState({ key: null, direction: "asc" });

  const handleSortClick = (key) => {
    const newSortConfig = { key, direction: "asc" };
    if (sortConfig.key === key && sortConfig.direction === "asc") {
      newSortConfig.direction = "desc";
    }

    setSortConfig(newSortConfig);

    const sortedBreeds = [...breeds].sort((a, b) => {
      const aValue = a[key];
      const bValue = b[key];

      if (newSortConfig.direction === "asc") {
        return a[key] > b[key] ? 1 : -1;
      } else {
        return a[key] < b[key] ? 1 : -1;
      }
    });

    setBreeds(sortedBreeds);
  };

  const getSortArrow = (key) => {
    if (sortConfig.key === key) {
      return sortConfig.direction === "asc" ? (
        <span className="arrow"></span>
      ) : (
        <span className="arrow"></span>
      );
    }
    return null;
  };

  const renderTableHeadCell = (label, key) => (
    <th key={key} onClick={() => handleSortClick(key)}>
      <div>
        <span>{label}</span> {getSortArrow(key)}
      </div>
    </th>
  );

  return (
    <thead>
      <tr>
        {renderTableHeadCell("ID", "id")}
        {renderTableHeadCell("Breed", "name")}
        {renderTableHeadCell("Avg. Weight", "avgWeight")}
        {renderTableHeadCell("Avg Lifespan", "avgLifespan")}
        {renderTableHeadCell("Avg Height", "avgHeight")}
        <th onClick={() => handleSortClick("isActive")}>
          <span>Active Breed</span> {getSortArrow("isActive")}
        </th>
      </tr>
    </thead>
  );
};

export default TableHead;

